import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image, Modal } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import SelectTimeModal from './SelectTimeModal'; // SelectTimeModal import 추가
import styles from './GymStyles';

const GymScreen = () => {
  const navigation = useNavigation();
  const [selectedDate, setSelectedDate] = useState(null);
  const [isTimeModalVisible, setTimeModalVisible] = useState(false);
  const [selectedTimes, setSelectedTimes] = useState([]);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleNavigateToSelectDateScreen = () => {
    navigation.navigate('SelectDateScreen');
  };
  const handleTimeButtonClick = (selectedIcon) => {
    // 아이콘에 따라 시간 선택 모달을 열기
    setSelectedIcon(selectedIcon);
    setTimeModalVisible(true);
  };
  const handleIcon1Click = () => {
    // 풋살장 아이콘 클릭 시 실행되는 함수
    setSelectedIcon('풋살장');
    // 풋살장 아이콘 클릭 시 모달 표시
    setTimeModalVisible(true);
  };

  const handleIcon2Click = () => {
    // 체육관 아이콘 클릭 시 실행되는 함수
    setSelectedIcon('체육관');
    // 체육관 아이콘 클릭 시 모달 표시
    setTimeModalVisible(true);
  };

  const handleIcon3Click = () => {
    // 운동장 아이콘 클릭 시 실행되는 함수
    setSelectedIcon('운동장');
    // 운동장 아이콘 클릭 시 모달 표시
    setTimeModalVisible(true);
  };

  const handleSelectTime = (times) => {
    // 시간 선택 모달에서 호출되는 콜백 함수
    setSelectedTimes(times);
    setTimeModalVisible(false); // 모달 닫기
  };

  // ... (이전 코드)

  return (
    <View style={styles.container}>
      <View style={styles.headerContainer}>
        <Text style={styles.header}>체육관</Text>
      </View>
      
      <Image
        source={require('./GymScreen.jpg')}
        style={styles.image}
      />
      
      <View style={styles.reservationStatus}>
        <Text style={styles.reservationText}>예약 확인: {selectedTimes.length > 0 ? selectedTimes.map(time => `${time.startTime} - ${time.endTime}`).join(', ') : '예약되지 않음'}</Text>
      </View>

      {/* 아이콘 1 */}
      <TouchableOpacity style={[styles.icon, { left: 270, top: 125 }]} onPress={handleIcon1Click}>
        <Text style={styles.iconText}>풋살장</Text>
      </TouchableOpacity>
      
      {/* 아이콘 2 */}
      <TouchableOpacity style={[styles.icon, { left: 270, top: 240 }]} onPress={handleIcon2Click}>
        <Text style={styles.iconText}>체육관</Text>
      </TouchableOpacity>
      
      {/* 아이콘 3 */}
      <TouchableOpacity style={[styles.icon, { left: 150, top: 370 }]} onPress={handleIcon3Click}>
        <Text style={styles.iconText}>운동장</Text>
      </TouchableOpacity>

      {/* 시간 선택 버튼 */}
      <TouchableOpacity style={styles.selectTimeButton} onPress={() => handleTimeButtonClick(selectedIcon)}>
        <Text style={styles.selectTimeButtonText}>시간 선택</Text>
      </TouchableOpacity>

      {/* 시간 선택 모달 */}
      <Modal
  animationType="slide"
  transparent={true}
  visible={isTimeModalVisible}
  onRequestClose={() => {
    setTimeModalVisible(false);
  }}
>
  <View style={styles.modalContainer}>
    <View style={styles.modalContent}>
      <SelectTimeModal
        visible={isTimeModalVisible}
        onClose={() => {
          setTimeModalVisible(false);
        }}
        onSelectTime={handleSelectTime}
        initialSelectedTimes={selectedTimes}
      />
    </View>
  </View>
</Modal>
    </View>
  );
};

export default GymScreen;
