import { StyleSheet } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
const LostStyles = StyleSheet.create({
    mainContainer: {
        flex: 1,
      },
      headerContainer: {
        height: hp('10%'),
        backgroundColor: '#f8f9fa',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
      },
      titleBox: {
        backgroundColor: 'transparent',
        borderBottomWidth: 1,
        width: '100%',
        padding: 10,
        borderColor: '#a0a0a0',
        alignItems: 'center',
        borderRadius: 5,
      },
      titleText: {
        fontSize: 18,
        fontWeight: 'bold',
      },

      bodyContainer: {
        backgroundColor: '#f8f9fa',
        height: hp('70%'), // 높이가 컨텐츠에 따라 동적으로 조절되도록 설정
      },
      footerContainer: {
        height: hp('15%'),
        backgroundColor: '#f8f9fa',
        alignItems: 'center',
        justifyContent: 'center',
      },
      addButtonContainer: {
        backgroundColor: 'transparent',
        borderWidth: 1,
        borderColor: '#a0a0a0',
        borderWidth: 2,
        padding: 10,
        borderRadius: 5,
        width: '70%',
        margin: '10px',
        
      },
      
      container: {
        flex: 1,
      },

      photoheader: {
        height: hp('50%'),
        backgroundColor: '#f8f9fa',
        alignItems: 'center',
        justifyContent: 'center',

      },
      

      photoButtonsContainer: {
        flexDirection: 'column', 
        backgroundColor: '#f8f9fa',
        height: hp('10%'),
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center'
      },

      photoButtontop: {
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#B0E0E6',
        borderWidth: 2,
        borderColor: '#ADD8E6',
        borderRadius: 10,
        width: '50%',
        height: '70%'
        
        
      },
      
      photobody: {
        backgroundColor: '#f8f9fa',
        height: hp('30%'), 
        flexDirection: 'row',
        
        
        
      },
      bodyleft: {
        width: '50%',
        flexDirection: 'column',
        
        alignItems: 'center'
      },

      label: {
        margin: 10,
      },

      input: {
        borderWidth: 1,
        borderColor: '#a0a0a0',
        padding: 10,
        margin: 5,
        width: '80%',
        textAlign: 'center'
      },

      inputsecond: {
        borderWidth: 1,
        textAlign: 'center',
        width: '80%',
        borderColor: '#a0a0a0',
        padding: 10,
        margin: 5,
      },

      bodyright: {
        width: '50%',
        flexDirection: 'column',
        alignItems: 'center',
      },
      itemContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        borderBottomWidth: 1,
        borderColor: '#e0e0e0',
        marginTop: 5,
        borderRadius: 5, // 컨테이너 모서리 둥글게 변경
        height: '100%',
        width: '100%',
     
        flex: 1
      },
      itemInfoContainer: {
        width: '100%',
        height: '100%',
        

      },
      itemInfoTop: {
        height: '50%',
        flexDirection: 'row',
        width: '100%',
        borderBottomWidth: 1,
        borderColor: '#bdbebd',
      },
      Infotitle: {
        width: '20%',
        height: '100%',
        borderRightWidth: 1,
        borderColor: '#bdbebd',
        justifyContent: 'center',
        textAlign: 'center',
        alignItems: 'center',
      },
      Infomain: {
       width: '50%',
       height: '100%',
       justifyContent: 'center',
       alignItems: 'flex-start',
       textAlign: 'center',
       padding: 5,
      },
      Infobottomtitle: {
        width: '20%',
        height: '100%',
        borderRightWidth: 1,
        borderColor: '#bdbebd',
        justifyContent: 'center',
        textAlign: 'center',
        alignItems: 'center',
      },
      Infobottommain: {
        padding: 5,
        width: '50%',
        height: '100%',
        justifyContent: 'center',
        alignItems: 'flex-start',
        textAlign: 'center',
       
      },

      itemInfobottom: {
        height: '50%',
        flexDirection: 'row',
        width: '100%',
        
      },


      itemImage: {
        width: 130, // 이미지 크기 조정
        height: 130, // 이미지 크기 조정
        borderRadius: 5, // 이미지 모서리를 둥글게
        margin: 9,
        borderWidth: 1, // 이미지 테두리 추가
        borderColor: '#ccc', // 이미지 테두리 색상 변경
    },

      photobottom: {
        
        height: hp('12%'),
        bottom: 0,
        position: 'absolute',
        alignItems: 'center',
        margin: 0,
        width: '100%'
      }
  });

  export default LostStyles;
