import React from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, ImageBackground } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import GymScreen from './Gym/GymScreen';
import SelectDateScreen from './Gym/SelectDateScreen';
import MainScreenComponent from './MainScreenComponent';
import Lostmain from './Lost/Lostmain';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const MainScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
     
      <View style={styles.headerContainer}>
        <View style = {styles.imageContainer}>
          <ImageBackground
            source={require('./../assets/GWNU-LOGO.png')} // 이미지를 로컬 'assets' 폴더에서 불러옴
            style={styles.imageStyle}
            resizeMode="cover"
          >
          </ImageBackground>
        </View>

        <View style = {styles.textContainer}>
          <Text style={styles.textStyle}>국립 강릉원주대학교</Text>
        </View>
        
        <Text style={styles.subHeader}>GANGNEUNG-WONJU NATIONAL UNIVERSITY</Text>
      </View>
      
      <View style={styles.loginFormContainer}>
        <TextInput
          style={styles.input}
          placeholder="아이디"
        />
        <TextInput
          style={styles.input}
          placeholder="비밀번호"
          secureTextEntry={true}
        />
        <TouchableOpacity
          style={styles.loginButton}
          onPress={() => navigation.navigate('MainScreenComponent')}
        >
          <Text style={styles.loginButtonText}>로그인</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.signupButton}
        >
          <Text style={styles.signupButtonText}>회원가입</Text>
        </TouchableOpacity>
      </View>
    </View>
    
  );
};

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Main">
        <Stack.Screen
          name="Main"
          component={MainScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="MainScreenComponent"
          component={MainScreenComponent}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="GymScreen"
          component={GymScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="SelectDateScreen"
          component={SelectDateScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen 
          name="Lostmain" 
          component={Lostmain} 
          options={{ headerShown: false }} 
        />

      </Stack.Navigator>
    </NavigationContainer>

    
  );

};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },

  headerContainer: {
    backgroundColor: 'rgb(0, 150, 200)',
    width: '100%',
    height: '12%',
    position: 'absolute',
    flexDirection: 'column',
    alignItems: 'center',
    top: 0,
    justifyContent: 'center',
  },
  
  imageContainer: {
    position: 'absolute',
    alignItems: 'flex-start',
    left: 10,

  },
  imageStyle: {
    width: 50, 
    height: 50,
    alignItems: 'flex-start',
  },
/*
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    top: 46,
    
  },
  */
  textContainer: {
    
    fontWeight: 'bold',
    alignItems: 'center', // 텍스트를 수평으로 중앙에 배치합니다
  },
  subHeader: {
    fontSize: 8,
    top: 47,
    fontWeight: 'bold',
    color: 'white',
  },
  loginFormContainer: {
    marginTop: 100,
    alignItems: 'center',
  },
  input: {
    width: '80%',
    backgroundColor: 'lightgray',
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginBottom: 10,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: 'gray',
  },
  loginButton: {
    backgroundColor: 'blue',
    width: '80%',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 1,
    borderColor: 'gray',
  },
  loginButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  signupButton: {
    marginTop: 10,
  },
  signupButtonText: {
    color: 'blue',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default App;
