import React from 'react';
import { getDatabase, ref, set, get } from 'firebase/database';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, ImageBackground, Alert, BackHandler } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import Icon from 'react-native-vector-icons/Ionicons'; // 사용할 아이콘 라이브러리 선택

const SignUpScreen = () => {
  const navigation = useNavigation();
  const [id, setId] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [confirmPassword, setConfirmPassword] = React.useState('');
  const [name, setName] = React.useState('');
  const [phone, setPhone] = React.useState('');

  const validatePassword = (pw) => {
    const re = /^(?=.*[a-zA-Z])(?=.*[0-9])(?=.*[!@#$%^&*]).{4,12}$/;
    return re.test(pw);
  };

  const handleSignUp = async () => {
    const db = getDatabase();

    if (password !== confirmPassword) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }
    if (!validatePassword(password)) {
      alert("비밀번호는 영어, 숫자, 특수기호(!@#$%^&*)를 포함하여 4~12자리로 구성되어야 합니다.");
      return;
    }

    // 학번이 데이터베이스에 있는지 확인
    const userRef = ref(db, 'users/' + id);
    const snapshot = await get(userRef);

    if (snapshot.exists()) {
      alert("이미 등록된 학번입니다.");
    } else {
      set(userRef, {
        password: password,  // 주의: 평문 저장은 위험합니다. 암호화 필요!
        name: name,
        phone: phone
      });
      alert("회원가입이 성공적으로 완료되었습니다.");
    }
    
    

  };

  return (
    <View style={styles.container}>
      <View style = {styles.headerContainer}>
        <View style = {styles.headerbutton}>
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <View style={styles.backbutton}>
              <Icon name="ios-arrow-back" size={35}/> 
            </View>
          </TouchableOpacity>
        
        </View>

        <View style = {styles.headermid}>
        <Text style={styles.titlestyle}>회원가입</Text>
        </View>
        <View style={styles.spacer}>
        </View>     
        
      </View>

      <View style = {styles.bodyContainer}>
        
          <Text style = {styles.first}>학번</Text>
          <TextInput
            style={styles.input}
            value={id}
            onChangeText={setId}
          />
       
      <Text style = {styles.second}>비밀번호</Text>
      <TextInput
        style={styles.input}
        placeholder="영어,숫자,특수기호로 이루어진 4~12자리 PW"
        placeholderTextColor="black"
        secureTextEntry={true}
        value={password}
        onChangeText={setPassword}
      />
      <TextInput
        style={styles.input}
        placeholder="비밀번호 확인"
        placeholderTextColor="black"
        secureTextEntry={true}
        value={confirmPassword}
        onChangeText={setConfirmPassword}
      />
      
      <TextInput
        style={styles.input}
        placeholder="이름"
        placeholderTextColor="black"
        value={name}
        onChangeText={setName}
      />
      <TextInput
        style={styles.input}
        placeholder="전화번호"
        placeholderTextColor="black"
        value={phone}
        onChangeText={setPhone}
      />
      <TouchableOpacity
        style={styles.signupButton}
        onPress={handleSignUp}
      >
        <Text style={styles.signupButtonText}>회원가입</Text>
      </TouchableOpacity>
      </View>

      <View style = {styles.bottomContainer}>
      <ImageBackground
            source={require('./../assets/teamLogo.png')}
            style={styles.bottomImageStyle}
            resizeMode="contain" 
        />
      </View> 
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    
  },
  headerContainer: {
    width: '100%',
    flex: 1,
    height: hp('15%'),
    position: 'absolute',
    flexDirection: 'row',
    top: 0,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'green',
  },
  headerbutton: {
    width: wp('22%'),
    flex: 1,
    left: 0,
    height: '100%',
    backgroundColor: 'skyblue',
    justifyContent: 'center',
    alignItems: 'baseline',
    
  },

  headermid: {
    width: wp('22%'),
    flex: 1,
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'blue',
  },

  titlestyle: {
    color: 'black',
    fontSize: wp('8%'),
    fontWeight: 'bold',
    top: hp('2%'),
  },

  spacer: {
    width: wp('18%'),
    backgroundColor: 'green',
    height: '100%',
    flex: 1,
  },

  backbutton: {

    top: hp('2%'),
    left: wp('1%'),
  },
//body

  bodyContainer: {
    width: '100%',
    height: hp('70%'),
   
    
    
  },
  
  first: {
    fontWeight: 'bold',
  },

  bottomContainer: {
    position: 'absolute',
    bottom: 0,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    height: hp('16%'),
    backgroundColor: 'green',
  },

  bottomImageStyle: {
    width: wp('40%'),
    height: hp('15%'),
  },


  input: {
    width: hp('40%'),
    height: hp('7%'),
    
  },
  signupButton: {
    marginTop: 10,
    backgroundColor: 'blue',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  signupButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },

  

});

export default SignUpScreen;
