import React, { useState, createContext, useContext, useEffect, useRef } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Image, ScrollView, Platform, PermissionsAndroid, Modal, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { Camera } from 'expo-camera';
import styles from './LostStyles';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';

const Stack = createStackNavigator();
const LostItemContext = createContext();

function AddItemScreen({ navigation }) {
  const [itemType, setItemType] = useState('');
  const [location, setLocation] = useState('');
  const [photoUri, setPhotoUri] = useState(null);
  const [photoTaken, setPhotoTaken] = useState(false);
  const [hasCameraPermission, setHasCameraPermission] = useState(null);
  const [showPhotoOptions, setShowPhotoOptions] = useState(false);
  const { addItem } = useContext(LostItemContext);
  const cameraRef = useRef(null);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestCameraPermissionsAsync();
      setHasCameraPermission(status === 'granted');
    })();
  }, []);

  const takePicture = async () => {
    if (cameraRef.current) {
      const options = { quality: 0.5, base64: true, skipProcessing: false };
      const data = await cameraRef.current.takePictureAsync(options);
      setPhotoUri(data.uri);
      setShowPhotoOptions(false);
      setPhotoTaken(true);
    }
  };

  const handleChoosePhoto = () => {
    setShowPhotoOptions(true);
  };

  const handleAddItem = () => {
    if (itemType && location && photoUri) {
      addItem({ itemType, location, photoUri });
      navigation.goBack();
    }
  };
  const reshoot = () => {
    if (cameraRef.current) {
      cameraRef.current.resumePreview(); // 카메라 미리보기 다시 시작
    }
    setPhotoUri(null); // 사진 URI 초기화
    setPhotoTaken(false);
  };

  if (hasCameraPermission === null) {
    return <View />;
  }

  if (hasCameraPermission === false) {
    return <Text>No access to camera</Text>;
  }



  return (
    <KeyboardAwareScrollView  style={styles.container}>
      <View style={styles.photoheader}>
        {photoUri ? (
          <Image source={{ uri: photoUri }} style={{ width: 300, height: 300 }} />
        ) : (
          <Camera ref={cameraRef} style={{ width: 350, height: 350 }} />
        )}
          
      </View>
      
        <View style={styles.photoButtonsContainer}>
          <View style={styles.photoButtontop}>
            {!photoTaken ? (
              <TouchableOpacity onPress={takePicture} style={{ alignItems: 'center'}}>
                <Text style={{ fontSize: 23, color: '#666666'}}>
                  눌러서 촬영
                </Text>
              </TouchableOpacity>
              ) : (
              <TouchableOpacity onPress={reshoot} style={{ alignItems: 'center'}}>
                <Text style={{ fontSize: 23, color: '#666666'}}>재촬영</Text>
              </TouchableOpacity>
            )}
          </View>
            
        </View>
     
      <View style={styles.photobody}>
        <View style={styles.bodyleft}>
          <Text style={styles.label}>분실물의 종류</Text>
            <TextInput
              style={styles.input}
              placeholder="분실물의 종류 입력"
              placeholderTextColor="lightgray"
              value={itemType}
              onChangeText={(text) => setItemType(text)}
            />
        </View>
        <View style={styles.bodyright}>
          <Text style={styles.label}>습득 장소</Text>
            <TextInput
              style={styles.inputsecond}
              placeholder="습득 장소 입력"
              placeholderTextColor="lightgray"
              value={location}
              onChangeText={(text) => setLocation(text)}
            />
        </View>
      </View>
      <Modal
        visible={showPhotoOptions}
        animationType="slide"
        transparent={true}
      >
        <View style={styles.modalContainer}>
          <View style={styles.photoOptions}>
            <Button title="사진 촬영하기" onPress={takePicture} />
            <Button title="사진 선택하기" onPress={handleChoosePhoto} />
            <Button title="닫기" onPress={() => setShowPhotoOptions(false)} />
          </View>
        </View>
      </Modal>
      <View style={styles.photobottom}>
        <TouchableOpacity
          onPress={handleAddItem}
          style={{
            backgroundColor: '#1E90FF',
            
            borderColor: '#f8f9fa',
            borderRadius: 2,
            padding: 10,
            width: '100%',
            height: '100%',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Text style={{ fontSize: 18, color: '#191970' }}>등록하기</Text>
        </TouchableOpacity>
      </View>
    </KeyboardAwareScrollView >
  );
}

function LostmainScreen({ navigation }) {
  const { items } = useContext(LostItemContext);

  return (
    <View style={styles.mainContainer}>
      <View style={styles.headerContainer}>
        <View style={styles.titleBox}>
          <Text style={styles.titleText}>분실물 목록</Text>
        </View>
        
      </View>
      <ScrollView style={styles.bodyContainer}>
        {items.map((item, index) => (
        <View key={index} style={styles.itemContainer}>
          <Image source={{ uri: item.photoUri }} style={styles.itemImage} />
          <View style={styles.itemInfoContainer}>
            <View style={styles.itemInfoTop}>
              <View style={styles.Infotitle}>
                <Text>분실물</Text>
              </View>
              <View style={styles.Infomain}>
                <Text style={styles.itemLabel} numberOfLines={1}>
                  {item.itemType} 
                </Text>
              </View>
            </View>
            <View style={styles.itemInfobottom}>
              <View style={styles.Infobottomtitle}>
                <Text>습득장소</Text>
              </View>
              <View style={styles.Infobottommain}>
                <Text style={styles.itemLabel} numberOfLines={1}>
                  {item.location}
                </Text>
              </View>
            </View>
          </View>
        </View>
        ))}
        
      </ScrollView>
      <View style={styles.footerContainer}>
          <View style={styles.addButtonContainer}>
            <Button title="등록하기" onPress={() => navigation.navigate('AddItem')} color="black"/>
          </View>
        </View>
    </View>
  );
}

function Lostmain() {
  const [items, setItems] = useState([]);

  const addItem = (item) => {
    setItems([...items, item]);
  };

  return (
    <LostItemContext.Provider value={{ items, addItem }}>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen 
          name="Home" 
          component={LostmainScreen} 
          options={{ title: '분실물 관리' }} 
        />
        <Stack.Screen 
          name="AddItem" 
          component={AddItemScreen} 
          options={{ title: '분실물 등록' }} 
        />
      </Stack.Navigator>
    </LostItemContext.Provider>
  );
}

export default Lostmain;
