import React from 'react';
import { UserProvider } from './UserContext';
import Navigation from './Navigation';
import { useFonts } from 'expo-font';

const App = () => {
  // 폰트 로드
  const [fontsLoaded] = useFonts({
    Jalnan2TTFRegular: require('../assets/fonts/Jalnan2TTF.ttf'),
    Jalnan2TTFBold: require('../assets/fonts/Jalnan2TTF.ttf'), // 파일 이름 수정
  });
  console.log("Fonts loaded:", fontsLoaded);

  // 폰트 로드 상태에 따라 컴포넌트 렌더링
  if (!fontsLoaded) {
    return null; // 폰트 로드 중일 때는 아무것도 렌더링하지 않음
  }

  // 폰트 로드 완료 후 앱 렌더링
  return (
    <UserProvider>
      <Navigation/>
    </UserProvider>
  );
};

export default App;
