import React from 'react';
import { getDatabase, ref, set, get } from 'firebase/database';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, ImageBackground, Alert, BackHandler } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import Icon from 'react-native-vector-icons/Ionicons'; // 사용할 아이콘 라이브러리 선택
import { LinearGradient } from 'expo-linear-gradient';
const SignUpScreen = () => {
  const navigation = useNavigation();
  const [id, setId] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [confirmPassword, setConfirmPassword] = React.useState('');
  const [name, setName] = React.useState('');
  const [phone, setPhone] = React.useState('');

  const validatePassword = (pw) => {
    const re = /^(?=.*[a-zA-Z])(?=.*[0-9])(?=.*[!@#$%^&*]).{4,12}$/;
    return re.test(pw);
  };

  const handleSignUp = async () => {
    const db = getDatabase();

    if (password !== confirmPassword) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }
    if (!validatePassword(password)) {
      alert("비밀번호는 영어, 숫자, 특수기호(!@#$%^&*)를 포함하여 4~12자리로 구성되어야 합니다.");
      return;
    }

    // 학번이 데이터베이스에 있는지 확인
    const userRef = ref(db, 'users/' + id);
    const snapshot = await get(userRef);

    if (snapshot.exists()) {
      alert("이미 등록된 학번입니다.");
    } else {
      set(userRef, {
        password: password,  // 주의: 평문 저장은 위험합니다. 암호화 필요!
        name: name,
        phone: phone
      });
      alert("회원가입이 성공적으로 완료되었습니다.");
    }
    
    

  };

  return (
    <View style={styles.container}>
      <LinearGradient style={styles.headerContainer} colors = {['#a0a0a0','#f8f9fa', '#f8f9fa']}>
        <View style = {styles.headerbutton}>
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <View style={styles.backbutton}>
              <Icon name="ios-arrow-back" size={35}/> 
            </View>
          </TouchableOpacity>
        
        </View>

        <View style = {styles.headermid}>
          <Text style={styles.titlestyle}>회원가입</Text>
        </View>
        <View style={styles.spacer}>
        </View>     
      </LinearGradient>
      
      <View style = {styles.bodyContainer}>
        
      </View>
      
      

      <View style = {styles.bottomContainer}>
        <ImageBackground
            source={require('./../assets/teamLogo.png')}
            style={styles.bottomImageStyle}
            resizeMode="contain" 
        />
      </View> 
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    
  },
  headerContainer: {
    width: '100%',
    flex: 1,
    height: hp('15%'),
    position: 'absolute',
    flexDirection: 'row',
    top: 0,
    alignItems: 'center',
    justifyContent: 'center',
    
  },
  headerbutton: {
    width: wp('22%'),
    flex: 1,
    left: 0,
    height: '100%',
    
    justifyContent: 'center',
    alignItems: 'baseline',
    
  },

  headermid: {
    width: wp('22%'),
    flex: 1,
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    
  },

  titlestyle: {
    color: 'black',
    fontSize: wp('6%'),
    fontWeight: 'bold',
    top: hp('2%'),
  },

  spacer: {
    width: wp('18%'),
    
    height: '100%',
    flex: 1,
  },

  backbutton: {

    top: hp('2%'),
    left: wp('1%'),
  },

  
  
  
  bottomContainer: {
    position: 'absolute',
    bottom: 0,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    height: hp('16%'),
    backgroundColor: '#f8f9fa',
  },

  bottomImageStyle: {
    width: wp('40%'),
    height: hp('15%'),
  },
  

});

export default SignUpScreen;
