import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, ImageBackground} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Icon from 'react-native-vector-icons/Ionicons'; // 사용할 아이콘 라이브러리 선택
import { getDatabase, ref, get } from 'firebase/database';
import { useUser, UserProvider } from './UserContext';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import LogoutButton from './LogoutButton';



const MainScreenComponent = ({ navigation }) => {
  const { userId } = useUser();
  const [name, setName] = useState('');
  useEffect(() => {
    if (userId) {
      const db = getDatabase();
      const userRef = ref(db, 'users/' + userId + '/name');

      get(userRef)
        .then((snapshot) => {
          if (snapshot.exists()) {
            setName(snapshot.val());
          } else {
            setName('데이터 없음');
          }
        })
        .catch((error) => {
          console.error('데이터 가져오기 오류:', error);
        });
    }
  }, [userId]);
  return (
    <UserProvider> 
      <View style={styles.container}>
        <LinearGradient style={styles.headerContainer} colors = {['#a0a0a0','#f8f9fa', '#f8f9fa']}>
          <View style={styles.imageContainer}>
            <ImageBackground
              source={require('./../assets/GWNU-LOGO.png')}
              style={styles.imageStyle}
              resizeMode="contain"
            />
          </View>

          <View style={styles.textContainer}>
            <Text style={styles.textStyle}>국립 강릉원주대학교</Text>
            <Text style={styles.subHeader}>GANGNEUNG-WONJU NATIONAL UNIVERSITY</Text>
          </View>
          <View style={styles.spacer}/>
        
        </LinearGradient>
        <View style = {styles.bodyContainer}>
          <View style={styles.bodyTopContainer}>
            <Text style={styles.namestyle}>{name}님 환영합니다.</Text>
            <LogoutButton /> 
          </View>
          <View style={styles.bodyMiddleContainer}>
            <TouchableOpacity
            
              style={styles.button}
              onPress={() => navigation.navigate('BusScreen')}
            >
              <View style = {styles.busicon}>
                <Text style={styles.buttonText}>버스 예약</Text>
                <ImageBackground
                  source={require('./../assets/busimage.png')}
                  style={styles.busimage}
                  resizeMode= 'center'
                >
                    
                </ImageBackground>
                
              </View>
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.button}
              onPress={() => navigation.navigate('GymScreen')}
          
            >
              <View style = {styles.gymicon}>
                <Text style={styles.buttonText}>체육관</Text>
                <ImageBackground
                  source={require('./../assets/gym.png')} //<a href="https://www.flaticon.com/kr/free-icons/" title="스포츠 아이콘">스포츠 아이콘  제작자: Freepik - Flaticon</a>
                  style={styles.gymimage}
                  resizeMode= "contain"
                >
                    
                </ImageBackground>
              </View>
            </TouchableOpacity>
            
            
          </View>
          <View style = {styles.bodymiddleTwo}>
              <TouchableOpacity
              
                style={styles.lostbutton}
                onPress={() => navigation.navigate('Lostmain')}
              >
                <View style={styles.losticon}>
                <Icon name="ios-search" size={35}/>
                <Text style={styles.buttonText}>분실물</Text>
                </View>
              </TouchableOpacity>
            </View>
          <View style={styles.bodyBottomContainer}>
            
            
          </View>

        </View>

        <View style={styles.bottomImageContainer}>
          <ImageBackground
              source={require('./../assets/teamLogo.png')}
              style={styles.bottomImageStyle}
              resizeMode="contain" 
          />
        </View>
      </View>
    </UserProvider>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerContainer: {
    
    
    width: '100%',
    
    height: hp('15%'),
    position: 'absolute',
    flexDirection: 'row',
    top: 0,
    alignItems: 'center',
    justifyContent: 'center',
  },
  subHeader: {
    fontSize: hp('1%'), 
    top: hp('2%'), 
    fontWeight: 'bold',
    color: '#868296',
  },
 
  imageContainer: {
    width: wp('22%'),
    
  },
  imageStyle: {
    top: hp('1%'),
    width: wp('16%'),
    height:hp('7%'),
    left: wp('9%'),
    
  },

  textContainer: {
    width: wp('60%'),
    alignItems: 'center',
  },
  

  textStyle: {
    fontSize: hp('3%'), 
    top: hp('1%'), 
    fontWeight: 'bold',
    alignItems: 'center',
    color: '#343a40',
  },
  spacer: {
    width: wp('18%'),
  },

  //bodyContainer
  bodyContainer: {
    height: hp('72%'),
    width: wp('100%'),
    
     
  },
  bodyTopContainer: {
    backgroundColor: '#f8f9fa',
    height: hp('10%'),
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start',
    

  },

  namestyle: {
    color: 'black',
    fontWeight: 'bold',
    fontSize: wp('5%'),
    margin: wp('5%'),
  },

  
  bodyMiddleContainer: {
    height: hp('35%'),
    backgroundColor: '#f8f9fa',
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'row',
    flex: 1,
    
  },

  bodymiddleTwo: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#f8f9fa',
    height: hp('20%'),
    
  },

  bodyBottomContainer: {
    height: hp('7%'),
    backgroundColor: '#f8f9fa',
     
  },

  busicon: {
    flexDirection: 'column', // 텍스트와 이미지를 가로로 나란히 배치
    alignItems: 'center'
  },
  busimage: {
    width: wp('40%'),
    height: hp('10%'),
  },
  gymimage: {
    width: wp('40%'),
    height: hp('10%'),
  },



  gymicon: {
    flexDirection: 'column', // 텍스트와 이미지를 가로로 나란히 배치
    alignItems: 'center',
    

  },

  losticon: {
    alignItems: 'center',

  },

  //bottom

  bottomImageContainer: {
    
    position: 'absolute',
    bottom: 0,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    height: hp('16%'),
    backgroundColor: '#f8f9fa',
  },

  bottomImageStyle: {
    width: wp('40%'),
    height: hp('15%'),
  },


  button: {
    borderColor: 'white',
    backgroundColor: 'white',
    borderRadius: 20,
    borderWidth: 1,
    height: hp('20%'),
    width: wp('40%'),
    margin: wp('4%'),
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: 'rgba(0, 0, 0, 0.3)', // 그림자 색상
    shadowOffset: { width: 0, height: 2 }, // 그림자 위치 (가로, 세로)
    shadowOpacity: 0.5, // 그림자 투명도
    shadowRadius: 5, // 그림자 둥근 정도
    
    
    
  },
  buttonText: {
    color: '#5F5F5F',
    fontWeight: 'bold',
    fontSize: wp('5%'),
  },
  lostbutton: {
    borderColor: 'white',
    backgroundColor: 'white',
    borderRadius: 20,
    borderWidth: 1,
    height: hp('11%'),
    width: wp('80%'),
    margin: wp('4%'),
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: 'rgba(0, 0, 0, 0.3)', // 그림자 색상
    shadowOffset: { width: 0, height: 2 }, // 그림자 위치 (가로, 세로)
    shadowOpacity: 0.5, // 그림자 투명도
    shadowRadius: 5, // 그림자 둥근 정도
   
  },


  
  goBackButton: {
    backgroundColor: 'lightgray',
    marginTop: 10,
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: 'gray',
  },
  
});

export default MainScreenComponent
