import React from 'react';
import { View, Text, TouchableOpacity, ImageBackground, Alert } from 'react-native';
import { useUser, UserProvider } from '../UserContext';
import Icon from 'react-native-vector-icons/Ionicons';
import { useNavigation } from '@react-navigation/native';
import { StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';  // expo-linear-gradient import
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';

const iconsData = [
  { id: 1, name: '풋살장', engName: 'FootballCenter', position: { top: 55 } },
  { id: 2, name: '체육관', engName: 'SportsCenter', position: { top: 180 } },
  { id: 3, name: '운동장', engName: 'Ground', position: { top: 300 } },
];

const GymRvmain = () => {
  const { userId } = useUser(); 
  const navigation = useNavigation();
  const handleIconClick = (engName) => {
    navigation.navigate(engName);
  };  

  return (
    <UserProvider>
      <View>
        <View style={styles.header}>
          <LinearGradient style={styles.headerContainer} colors={['#DDF3FF', '#DDF3FF']}>
            <View style={styles.imageContainer}>
              <ImageBackground
                source={require('../../assets/GWNU-LOGO.png')}
                style={styles.imageStyle}
                resizeMode="contain"
              />
            </View>
            <View style={styles.textContainer}>
              <Text style={styles.textStyle}>국립 강릉원주대학교</Text>
              <Text style={styles.subHeader}>GANGNEUNG-WONJU NATIONAL UNIVERSITY</Text>
            </View>
            <View style={styles.spacer}/>
          </LinearGradient>
        </View>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <View>
            <Icon name="ios-arrow-back" size={100}/> 
          </View>
        </TouchableOpacity>
        <View>
          {userId && <Text>{`${userId}님 환영합니다!`}</Text>}
        </View>
        <View>
          {iconsData.map((icon) => (
            <TouchableOpacity
              key={icon.id}
              onPress={() => handleIconClick(icon.engName)}>
              <Text>{icon.name}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </View>
    </UserProvider>
  );
};

const styles = StyleSheet.create({
  header: {
    width: '100%',
    height: hp('20%'),
  },
  headerContainer: {
    width: '100%',
    height: hp('15%'),
    position: 'absolute',
    flexDirection: 'row',
    top: 0,
    alignItems: 'center',
    justifyContent: 'center',
  },
  imageContainer: {
    width: ('22%'),
  },
  imageStyle: {
    width: wp('22%'),
    height: '100%',
  },
  textContainer: {
    width: wp('60%'),
    alignItems: 'center',
  },
  textStyle: {
    fontSize: hp('3%'), 
    top: hp('1%'), 
    fontWeight: 'bold',
    alignItems: 'center',
    color: '#343a40',
  },
  subHeader: {
    fontSize: hp('1%'), 
    top: hp('2%'), 
    fontWeight: 'bold',
    color: '#868296',
  },
});

export default GymRvmain;
