import { StyleSheet } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';

const GymStyles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerContainer: {
    width: '100%',
    height: hp('15%'),
    position: 'absolute',
    flexDirection: 'row',
    top: 0,
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1,
  },
  headerbutton: {
    width: wp('30%'),
    flex: 1,
    left: 0,
    height: '100%',
    alignItems:'baseline',
    justifyContent: 'center',
    position: 'absolute',
  },
  backbutton: {
    
    top: hp('1%'),
    left: wp('1%'),
    
  },

  LogoimageContainer: {
    width: wp('20%'),
    left: wp('4%'),
  },
  LogoimageStyle: {
    top: 12,
    left: wp('8%'),
    width: wp('15%'),
    height: hp('7%'),
  },
  textContainer: {
    left: wp('4%'),
    width: wp('60%'),
    top: 0,
    alignItems: 'center',
  },
  textStyle: {
    fontSize: hp('3%'),
    top: hp('1%'),
    fontWeight: 'bold',
    alignItems: 'center',
    color: '#343a40',
  },
  subHeader: {
    fontSize: hp('1%'),
    top: hp('2%'),
    fontWeight: 'bold',
    color: '#868296',
  },
  spacer: {
    width: wp('18%'),
  },
  bodyContainer: {
    flex: 3,
    height: hp('40%'),
    width: wp('100%'),

  },
  bodyTopContainer: {
    backgroundColor: '#f8f9fa',
    height: hp('15%'),
    flexDirection: 'row',
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  bodyMiddleContainer: {
    height: hp('40%'),
    backgroundColor: '#f8f9fa',
    justifyContent: 'center',
  },
  Gymimage: {
    top: 82,
    width: '100%',
    height: 450,
  },
  
  icon: {
    position: 'absolute',
    backgroundColor: 'lightgray',
    width: 50,
    height: 50,
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
  },
  uncheckedIcon: {
    backgroundColor: 'rgba(255, 255, 255, 0.7)', // 반투명한 흰색
  },
  checkedIcon: {
    backgroundColor: 'rgba(0, 128, 0, 0.7)', // 반투명한 초록색
    borderWidth: 2, // 체크된 상태에서의 테두리 굵기
  },
  iconText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
  },
  bodyBottomContainer: {
    height: hp('30%'),
    backgroundColor: '#f8f9fa',
    flexDirection: 'column', 
  },
  reservationDetailContainer: {
    flex: 1,
    width: '100%',
    backgroundColor: '#f8f9fa',
    alignItems: 'center',
    justifyContent: 'center',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  reservationDetailTitle: {
    fontWeight: 'bold',
  },
  reservationDetailText: {
    color: '#495057',
  },
  
  bottomBar: {
    flex: 1,
    width: '100%',
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'space-between',
    flexDirection: 'row',
    paddingHorizontal: 20,
    paddingTop: 10
  },
  greyText: {
    color: 'grey'
  },
  reservationCancelButton: {
    marginLeft: 10,
    backgroundColor: 'red',
    padding: 10,
    borderRadius: 5
  },
  activeButton: {
    backgroundColor: '#4CAF50',
    padding: 10,
    borderRadius: 5
  },
  inactiveButton: {
    backgroundColor: 'grey',
    padding: 10,
    borderRadius: 5
  },
  
});

export default GymStyles;
