import React, { useState, createContext, useContext } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

const LostItemContext = createContext();

function AddItemScreen({ navigation }) {
  const [itemType, setItemType] = useState('');
  const [location, setLocation] = useState('');
  const { addItem } = useContext(LostItemContext);

  const handleAddItem = () => {
    if (itemType && location) {
      addItem({ itemType, location });
      navigation.goBack();
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>분실물 등록 화면</Text>
      <Text style={styles.label}>분실물의 종류</Text>
      <TextInput
        style={styles.input}
        placeholder="분실물의 종류 입력"
        placeholderTextColor="gray"
        value={itemType}
        onChangeText={(text) => setItemType(text)}
      />
      <Text style={styles.label}>습득 장소</Text>
      <TextInput
        style={styles.input}
        placeholder="습득 장소 입력"
        placeholderTextColor="gray"
        value={location}
        onChangeText={(text) => setLocation(text)}
      />
      <Button title="등록" onPress={handleAddItem} />
    </View>
  );
}

function LostmainScreen({ navigation }) {
  const { items, addItem } = useContext(LostItemContext);

  return (
    <View style={styles.mainContainer}>
      <Text>분실물 관리</Text>
      <Button title="등록" onPress={() => navigation.navigate('AddItem')} />
      <Text>등록된 물품:</Text>
      {items.map((item, index) => (
        <View key={index} style={styles.itemContainer}>
          <Text style={styles.itemLabel}>{item.itemType}</Text>
          <Text style={styles.itemLabel}>{item.location}</Text>
        </View>
      ))}
    </View>
  );
}

function Lostmain() {
  const [items, setItems] = useState([]);

  const addItem = (item) => {
    setItems([...items, item]);
  };

  return (
    <LostItemContext.Provider value={{ items, addItem }}>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen 
          name="Home" 
          component={LostmainScreen} 
          options={{ title: '분실물 관리' }} 
        />
        <Stack.Screen 
          name="AddItem" 
          component={AddItemScreen} 
          options={{ title: '분실물 등록' }} 
        />
      </Stack.Navigator>
    </LostItemContext.Provider>
  );
}

const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
    padding: 15,
  },
  rightAlignedItem: {
    alignItems: 'flex-end',  // 항목들을 오른쪽으로 정렬
    marginTop: 10,           // 각 항목 사이의 간격
  },
  itemLabel: {
    fontSize: 16,            // 텍스트 크기 설정
  },
  container: {
    flex: 1,
    paddingHorizontal: 15,
    paddingTop: 20,
    alignItems: 'flex-start', // UI를 왼쪽으로 정렬
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 15,  // 라벨과 입력 사이의 간격 조정
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
    marginVertical: 5,
  },
  itemContainer: {
    alignItems: 'flex-end',  
    borderBottomWidth: 1,  // 아래쪽 테두리
    borderColor: '#e0e0e0',  // 테두리 색상
    paddingVertical: 10,   // 상하 패딩
    marginTop: 10,           // 각 항목 사이의 간격
  },
  itemType: {
    flex: 1,
    paddingRight: 10,
  }
});

export default Lostmain;

