import { StyleSheet } from 'react-native';

const GymStyles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerContainer: {
    position: 'absolute',
    top: 40,
    backgroundColor: 'black',
    width: '100%',
    alignItems: 'center',
    height: 100,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginVertical: 17,
    color: 'white',
  },
  image: {
    top: -52,
    width: '100%',
    height: 450,
  },
  reservationStatus: {
    width: '100%',
    padding: 10,
    backgroundColor: 'lightgreen',
  },
  reservationText: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  icon: {
    position: 'absolute',
    backgroundColor: 'lightgray',
    width: 50,
    height: 50,
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
  },
  iconText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
  },
  selectTimeButton: {
    backgroundColor: 'lightgray',
    marginTop: 10,
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: 'gray',
  },
  selectTimeButtonText: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  modalContainer: {
    flex: 100,
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // 반투명한 배경
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  modalContent: {
    width: '95%', // 모달의 가로 너비 설정
    backgroundColor: 'white',
    borderRadius: 30,
    padding: 122,
  },
});

export default GymStyles;
